  // }

  return (
    <div>
      {/* <button>Click Me for console log</button> */}
      <NavBar peopleData={peopleData}></NavBar>
      <h1
        id="title"
        style={{
          textAlign: "center",
          color: "yellow",
          width: "200px",
          position: "absolute",
          top: "0px",
          display: "block",
        }}
      >
        STAR WARS
      </h1>
