import React from "react";

const YourCart = (props) => {
  const cartDataArray = props.cart;

  const produceCartCards = cartDataArray.map((element, index) => {
    return (
      <div
        className="shoppingCards"
        id={index}
        data-aos="fade-down"
        data-aos-easing="ease-in-out"
        data-aos-delay="50"
        data-aos-duration="1000"
        style={{ backgroundColor: "rgba(53, 73, 84, 0.3)", borderRadius: '' }}
      >
        {/* <div className="individualCards"> */}
        <h1>{element.name}</h1>

        <section>
          <img className="card-images" src={element.url1} alt=""></img>
        </section>
        <ul className="list">
          <li>{element.cost} Galactic Credits</li>
          <li>{element.description}</li>
        </ul>
        <br />
        <button
          style={{
            backgroundColor: "white",
            borderRadius: "100%",
            width: "30px",
            textAlign: "center",
            padding: "5px",
            position: "absolute",
            top: "-15px",
            left: "-15px",
          }}
          onClick={() => removeFromCart(index)}
        >
          X
        </button>
      </div>
    );
  });

  const removeFromCart = (index) => {
    const cartArr = cartDataArray.filter((element, i) => i !== index);
    props.setCart(cartArr);
  };

  const resetCart = () => {};

  return (
    <div style={{ color: "white", textAlign: "center" }}>
      This is your shopping cart
      <div className="cartItemsDiv">{produceCartCards}</div>
      <div id="resetCart">
        <button>Reset Your Cart!</button>
      </div>
    </div>
  );
};

export default YourCart;
