import React, { Suspense, useState, useEffect } from "react";
import NavBar from "./components/NavBar";
import { NavLink } from "react-router-dom";
import "./index.css";
import Pistols from "./components/Pistols";
import { Route } from "react-router-dom";
import firebase from "./library/firebase";
import LoadingSpinner from "./components/LoadingSpinner";
// import { collection, getDocs } from "firebase/firestore";

const db = firebase.firestore();

function App() {
  const [planetData, setPlanetData] = useState("");
  const [fullData, setFullData] = useState("");

  const arrayCheck = () => {
    console.log(fullData.documents);
  };

  const makeApiCall = async () => {
    try {
      const res = await fetch(
        "https://firestore.googleapis.com/v1/projects/star-wars-weapons-info/databases/(default)/documents/weapons"
      );
      const json = await res.json();
      setFullData(json);
      console.log(fullData.documents);
    } catch (err) {
      console.log(err.message);
    }
  };

  useEffect(() => {
    makeApiCall();
  }, []);

  // async function getApi() {
  //   const query = await getDocs(collection(db, "weapons"));

  //   query.forEach((doc) => {
  //     console.log(`${doc.id} => ${doc.data()}`);
  //   });
  // }
  // getApi();

  // const fetchData = () => {
  //   db.collection('weapons')
  // }

  return (
    <div>
      {/* <button>Click Me for console log</button> */}
      <NavBar fullData={fullData} arrayCheck={arrayCheck}></NavBar>

      <NavLink to="/">
        <h1
          id="title"
          style={{
            textAlign: "center",
            color: "yellow",
            width: "200px",
            position: "absolute",
            top: "0px",
            display: "block",
          }}
        >
          STAR WARS
        </h1>
      </NavLink>
      <h3
        className="thinnerStarWarsFont"
        style={{
          textAlign: "center",
          width: "500px",
          margin: "auto",
        }}
      >
        Lord Vader wants you for the Galactic Empire.
      </h3>
      <Route path="/pistols">
        <Pistols fullData={fullData} setFullData={setFullData}></Pistols>
      </Route>
    </div>
  );
}

export default App;
