import React from "react";
import { NavLink } from "react-router-dom";
import styles from "./NavBar.module.css";

const NavBar = (props) => {
  const [cart, setCart] = useState([]);

  const addToCart = (card) => {
    setCart((prevState) => [...prevState, card]);
  };

  return (
    <header className={styles.navbar}>
      <nav>
        <ul>
          <li>
            <NavLink activeClassName={styles.active} to="/pistols">
              Pistols
            </NavLink>
          </li>
          <li>
            <NavLink activeClassName={styles.active} to="/rifles">
              Rifles
            </NavLink>
          </li>
          <li>
            <NavLink activeClassName={styles.active} to="/cannons">
              Cannons
            </NavLink>
          </li>
        </ul>
        <div id="cartHolder">
          <img
            id="cart"
            src="https://img.icons8.com/material-rounded/48/ffffff/favorite-cart.png"
          />
          <h1 id="Cart-Items">{numbOfCartItems}</h1>
          <div id="Show-Carted">
            {cart.map((element) => {
              if (cart.length > 0) {
                return (
                  <div>
                    <h3>{element.name}</h3>
                    <h3>{element.cost}</h3>
                  </div>
                );
              }
            })}
          </div>
        </div>
        <div>
          <SearchBar></SearchBar>
        </div>
      </nav>
    </header>
  );
};

export default NavBar;
