  return (
    <header className={styles.navbar}>
      <nav>
        <ul>
          <li>
            <NavLink
              activeClassName={styles.active}
              to="/planets"
              //   onMouseEnter={mouseHoverBlue}
              //   onMouseLeave={mouseExit}
              data-aos="fade-in"
              data-aos-once="false"
            >
              Planets
            </NavLink>
          </li>
          <li>
            <NavLink
              activeClassName={styles.active}
              to="/characters"
              //   onMouseEnter={mouseHoverGreen}
              //   onMouseLeave={mouseExit}
              data-aos="fade-in"
              data-aos-once="false"
            >
              Characters
            </NavLink>
          </li>
          <li>
            <NavLink
              activeClassName={styles.active}
              to="/starships"
              data-aos="fade-in"
              data-aos-once="false"
            >
              Starships
            </NavLink>
          </li>
        </ul>
      </nav>
    </header>
  );
};

export default NavBar;
