import React, { useEffect, useState } from "react";
import { Splide, SplideSlide } from "@splidejs/react-splide";
import "@splidejs/splide/dist/css/splide.min.css";
import { NavLink } from "react-router-dom";
import styles from ".components/Bounties.module.css";

const Bounties = (props) => {
  return (
    <div className="bountyCard-Group">
      <Splide
        options={{
          speed: "3000",
          autoplay: true,
          type: "loop",
          pauseOnHover: false,
          wheel: true,
          drag: true,
          perPage: 3,
        }}
      >
        {props.bountyData.map((element) => {
          return (
            <SplideSlide
              data-aos="fade-up"
              data-aos-easing="ease-in-out"
              data-aos-delay="50"
              data-aos-duration="1000"
            >
              <div className="bountyCard">
                <img className="bounty-image" src={element.url1} alt=""></img>
                <h1>{element.name}</h1>
                <h3>Bounty: {element.bounty} credits</h3>
              </div>
            </SplideSlide>
          );
        })}
      </Splide>
      ;
    </div>
  );
};

export default Bounties;
