import React, { useState, useEffect } from "react";
import NavBar from "./components/NavBar";
import "./index.css";
import Planets from "./components/Planets";
import { Route } from "react-router-dom";

function App() {
  const [planetData, setPlanetData] = useState("");
  const [peopleData, setPeopleData] = useState("");
  const [shipData, setShipData] = useState("");

  useEffect(() => {
    console.log(`useEffect is in effect`);
    const makeApiCall = async () => {
      try {
        const res = await fetch(`https://swapi.dev/api/planets/`);
        const json = await planetRes.json();
        setPlanetData(planetJson);
        const peopleRes = await fetch(`https://swapi.dev/api/people/`);
        const peopleJson = await peopleRes.json();
        setPeopleData(peopleJson.results);
        const shipRes = await fetch(`https://swapi.dev/api/starships/`);
        const shipJson = await shipRes.json();
        setShipData(shipJson.results);
      } catch (err) {
        console.log(err.message);
      }
    };

    makeApiCall();
  }, []);

  return (
    <div>
      <NavBar></NavBar>
      <h1
        id="title"
        style={{
          textAlign: "center",
          color: "yellow",
          width: "200px",
          position: "absolute",
          top: "0px",
          display: "block",
        }}
      >
        STAR WARS
      </h1>
      <h3
        className="thinnerStarWarsFont"
        style={{
          textAlign: "center",
          width: "500px",
          margin: "auto",
        }}
      >
        May the Force be with you.
      </h3>
      {/* <button onClick={buttonFunction}>Click me for Data</button> */}
      <Route path="/planets">
        <Planets planetData={planetData}></Planets>
      </Route>
    </div>
  );
}

export default App;
